function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector("body"),t=document.querySelectorAll(".menu__item");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active"),c.classList.add("locked"))})),t.forEach((t=>{t.addEventListener("click",(()=>{s.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked"))}))}burgerMenu();